package variable;

public class Operation
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int a = 10;
		int b = 6;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
		double da = 10.0;
		double db = 6.1;
		
		System.out.println(da+db);
		System.out.println(da-db);
		System.out.println(da*db);
		System.out.println(da/db);
		System.out.println(da%db);
	}

}
